content.query:
let engine_ = dynamic('{p_engine}');
let sensorengine_ = dynamic('{_sensorengine}');
SecurityAlert
| where ProviderName == "IoTSecurity"
//| where TimeGenerated {TimeRange} 
| where tostring(engine_) has AlertSeverity or isempty(engine_)
| where tostring(sensorengine_) has ProductComponentName or isempty(sensorengine_)
| summarize event_count=count() by TimeGenerated
